mapper.insert:
INSERT INTO Product (

) VALUES (
		#{productID}
		,#{title}
		,#{content}
		,#{accountID}
		,#{categoryID}
		,#{optionID}
		,#{price}
		,\func NOW()
		,\func NULL
		,0
		,0
)

mapper.update:
UPDATE Product 
SET 
		updateTime = \func NOW()
		,#{title}
		,#{content}
		,#{categoryID}
		,#{optionID}
		,#{price}
WHERE
		productID = #{productID}

mapper.select:
        SELECT fileURL
        FROM File
		WHERE productID = #{productID}
